import React, { useState, useEffect, useRef } from "react";
import SockJS from "sockjs-client";
import Stomp from "stompjs";

function App() {
  const [log, setLog] = useState([]);
  const [input, setInput] = useState("");
  const ws = useRef(null);

  // Fetch initial log
  useEffect(() => {
    fetch("/api/remoting/log")
      .then((res) => res.json())
      .then((data) => setLog(data.map((msg, i) => ({ from: msg.startsWith("You:") ? "You" : "Agent", text: msg.replace(/^You: |^Agent: /, "") }))));
  }, []);

  // WebSocket connection
  useEffect(() => {
    const sock = new SockJS("/ws");
    const stompClient = Stomp.over(sock);
    ws.current = sock;
    stompClient.connect({}, () => {
      stompClient.subscribe("/topic/messages", (message) => {
        setLog((prev) => [...prev, { from: "Agent", text: message.body }]);
      });
    });
    return () => {
      if (stompClient) stompClient.disconnect();
    };
  }, []);

  const sendMessage = async () => {
    setLog((prev) => [...prev, { from: "You", text: input }]);
    await fetch("/api/remoting/send", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ message: input }),
    });
    setInput("");
  };

  return (
    <div style={{ maxWidth: 600, margin: "40px auto", fontFamily: "sans-serif" }}>
      <h2>Jenkins Remoting Web Demo</h2>
      <div style={{ border: "1px solid #ccc", padding: 16, minHeight: 200, background: "#fafafa" }}>
        {log.map((entry, i) => (
          <div key={i} style={{ margin: "8px 0" }}>
            <b>{entry.from}:</b> {entry.text}
          </div>
        ))}
      </div>
      <div style={{ marginTop: 16 }}>
        <input
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
          placeholder="Type a message to agent..."
          style={{ width: "70%", padding: 8 }}
        />
        <button onClick={sendMessage} style={{ padding: 8, marginLeft: 8 }}>
          Send
        </button>
      </div>
    </div>
  );
}

export default App;
